private void addText(Document document, String text, Font font) {
    if (font == null) {
        Chunk chunk = new Chunk(text == null ? "" : text);
        try {
            document.add(chunk);
        } catch (DocumentException e) {
            e.printStackTrace();
        }
    } else {
        font.setSize(12f);
        font.setStyle(Font.NORMAL);
        Chunk chunk = new Chunk(text == null ? "" : text, font);
        try {
            document.add(chunk);
        } catch (DocumentException e) {
            e.printStackTrace();
        }
    }
}
